sp_widget.client_script:
function() {
  var c = this;
	c.uiAction = function(action) {
		c.data.action = action;
		c.server.update().then(function() {
			c.data.action = undefined;
		})
	}
}

sp_widget.script:
(function() {

	data.sys_id = input.sys_id || $sp.getParameter("sys_id");

	//Query the table for the details

	var catItem =  new GlideRecord('sc_cat_item');
	catItem.addActiveQuery();
	catItem.addQuery('sys_id',data.sys_id);
	catItem.query();
	while (catItem.next()) {
	data.short_description = catItem.getValue('short_description');
	data.show_incident =  catItem.u_show_incident;

}

    data.show_incident = true;// item.u_show_incident;
	if (input && input.action) {
		var action = input.action;

		// If Incident table

			if (action == 'create') {
				gs.addInfoMessage("This is test");
				var inc = new GlideRecord('incident');
				inc.setValue('short_description',data.short_description);


				gr.setValue('incident_state', 6);
				gr.setValue('state', 6);
				gr.setValue('resolved_by', gs.getUserID());
				gr.update();
			}
			if (action == 'cancel') {
				// Do something else
			}

	}

})();
